INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251231, 'Dalfopristin', 'Lidocaine', 'Moderate', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by dalfopristin-quinupristin.', 'DDInter', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dalfopristin-quinupristin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if dalfopristin-quinupristin must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297564/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Procaine, Tetracaine, Clemastine, Tetracaine, Mepyramine, Diphenhydramine, Tripelennamine, Doxepin, Pheniramine, Promethazine, Bacitracin, More', NULL, 1767369485);
