INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251239, 'Dalfopristin', 'Lurasidone', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lurasidone and its active metabolite, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.  If a moderate CYP450 3A4 inhibitor is added to existing lurasidone therapy, the lurasidone dosage should be reduced by half.  Concomitant use of lurasidone and ritonavir- or cobicistat-boosted atazanavir is considered to be contraindicated.  Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, cognitive and motor impairment, seizures, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Lurasidone dosage should start at 20 mg/day and not exceed 80 mg/day when coadministered with moderate CYP450 3A4 inhibitors.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297572/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Latuda (lurasidone)." Sunovion Pharmaceuticals Inc  (2010):', 'Fluphenazine, Haloperidol, Thiothixene, Risperidone, Amisulpride, Prochlorperazine, Promazine, Thioridazine, Trifluoperazine, Paliperidone, Chlorpromazine, More', NULL, 1767369485);
