INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251294, 'Dalfopristin', 'Quinidine', 'Major', 'Based on in vitro inhibition data, coadministration with dalfopristin-quinupristin may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme, including certain agents that can cause prolongation of the QTc interval (e.g., cisapride, disopyramide, pimozide, quinidine, tacrolimus).', 'DDInter', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Metabolism', 'Given the potential for serious and life-threatening adverse cardiac events associated with increased plasma levels of agents that may prolong the QTc interval, concomitant use with dalfopristin-quinupristin should be avoided if they are known to be substrates of CYP450 3A4.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297636/', '[1] "Product Information. Synercid (dalfopristin-quinupristin)." Rhone Poulenc Rorer  (2001):', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251295, 'Dalfopristin', 'Quinine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors.  Patients should be monitored closely for adverse reactions associated with quinine such as hematologic toxicities and cardiac arrhythmias including torsade de pointes and atrial fibrillation.  Patients should be advised to contact their physician if they experience increased side effects such as headache, flushing, sweating, nausea, vomiting, diarrhea, abdominal pain, tinnitus, dizziness, vertigo, hearing impairment, blurred vision, vision impairment, and irregular heart rhythm.', 'Metabolism', 'Caution is advised if quinine is used in combination with potent and moderate CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297637/', '[1] Zhao XJ, Ishizaki T "Metabolic interactions of selected antimalarial and non-antimalarial drugs with the major pathway (3-hydroxylation) of quinine in human liver microsomes." Br J Clin Pharmacol 44 (1997):  505-11[2] Mirghani RA, Hellgren U, Westerberg PA, Ericsson O, Bertilsson L, Gustafsson LL "The roles of cytochrome P450 3A4 and 1A2 in the 3-hydroxylation of quinine in vivo." Clin Pharmacol Ther 66 (1999):  454-60[3] Zhao XJ, Ishizaki T "A further interaction study of quinine with clinically important drugs by human liver microsomes: determinations of inhibition constant (K-i) and type of inhibition." Eur J Drug Metab Pharm 24 (1999):  272-8[4] Wanwimolruk S, Paine MF, Pusek SN, Watkins PB "Is quinine a suitable 