INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251319, 'Dalfopristin', 'Solifenacin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of solifenacin, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.  Patients should be advised to contact their physician if they experience undue adverse effects of solifenacin such as severe abdominal pain or constipation for 3 or more days.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297665/', '[1] "Product Information. VESIcare (solifenacin)." GlaxoSmithKline  (2004):', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', NULL, 1767369485);
