INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251335, 'Dalfopristin', 'Ticagrelor', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Although administration with moderate CYP450 3A4 inhibitors is thought to be generally safe, it may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents.  Discontinuation of the concomitant medication may be required if an interaction is suspected.', 'Metabolism', 'Although administration with moderate CYP450 3A4 inhibitors is thought to be generally safe, it may be advisable to monitor pharmacologic response to ticagrelor more closely following the addition and withdrawal of these agents.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297683/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals  (2011):', 'Betrixaban, Clopidogrel, Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Warfarin, Caplacizumab, Argatroban, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251336, 'Dalfopristin', 'Tofacitinib', 'Moderate', 'Coadministration with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19 may significantly increase the plasma concentrations of tofacitinib, which is primarily metabolized by the former isoenzyme with minor contribution from the latter.', 'DDInter', 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19.  For example, the dose for patients receiving 10 mg twice daily should be reduced to 5 mg twice daily and the dose for patients receiving 5 mg twice daily should be reduced to 5 mg once daily.  For patients receiving 11 mg once daily of the extended-release formulation, the dose should be reduced to 5 mg once daily of the immediate-release formulation.  The dose for patients receiving 3.2 mg twice daily should be reduced to 3.2 mg once daily and the dose for patients receiving 4 mg twice daily should be reduced to 4 mg once daily.  Moderate inhibitors of CYP450 3A4 include amiodarone, aprepitant, ciprofloxacin, crizotinib, darunavir, dalfopristin-quinupristin, diltiazem, dronedarone, erythromycin, fluconazole, fusidic acid, grapefruit juice, imatinib, isavuconazonium, netupitant, and verapamil.  Potent inhibitors of CYP450 2C19 include fluconazole, fluvoxamine, esomeprazole, lansoprazole, and omeprazole.  Inhibitors of CYP450 2C19 alone are unlikely to substantially alter the pharmacokinetics of tofacitinib.', 'Metabolism', 'The dosage of tofacitinib should be reduced by 50% when used concomitantly with one or more medications that may result in both moderate inhibition of CYP450 3A4 and potent inhibition of CYP450 2C19.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297685/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group  (2012):', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Abatacept, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251337, 'Dalfopristin', 'Tolterodine', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the plasma concentrations of tolterodine, which is partially metabolized by the isoenzyme.  The possibility of prolonged and/or increased pharmacologic effects of tolterodine should be considered.  Although tolterodine is primarily metabolized by CYP450 2D6, there is some evidence that CYP450 3A4 may play a minor role, thus any alteration in its activity levels could conceivably affect the metabolism of tolterodine.', 'DDInter', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.  Close clinical and laboratory monitoring is advised whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.  Patients should be advised to notify their physician if they experience an irregular heartbeat, severe blurry vision, difficulty urinating, dry mouth, headache, drowsiness, dizziness, or GI upset.', 'Metabolism', 'The manufacturer recommends a maximum tolterodine dosage of 1 mg twice daily in patients receiving concomitant CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297686/', '[1] "Product Information. Detrol (tolterodine)." Pharmacia and Upjohn  (2001):[2] Brynne N, Forslund C, Hallen B, Gustafsson LL, Bertilsson L "Ketoconazole inhibits the metabolism of tolterodine in subjects with deficient CYP2D6 activity." Br J Clin Pharmacol 48 (1999):  564-72', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Papaverine, Phenyl salicylate, More', NULL, 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251338, 'Dalfopristin', 'Tolvaptan', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of tolvaptan, which is almost exclusively metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of tolvaptan with moderate CYP450 3A4 inhibitors should generally be avoided unless no alternatives exist and benefits outweigh potential risks.  A lower dosage of tolvaptan should be considered.  Serum sodium concentration and neurologic status should be closely monitored during coadministration.  Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death.  Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Metabolism', 'Concomitant use of tolvaptan with moderate CYP450 3A4 inhibitors should generally be avoided unless no alternatives exist and benefits outweigh potential risks.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297687/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc  (2009):', 'Conivaptan', NULL, 1767369485);
