INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251378, 'Paritaprevir', 'Acarbose', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297740/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences  (2013):[4] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences  (2014):[5] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LL