INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251438, 'Duvelisib', 'Paritaprevir', 'Moderate', 'Coadministration with duvelisib may increase the plasma concentrations of drugs that are primarily metabolized by the CYP450 3A4 isoenzyme.  The mechanism is reduced clearance due to inhibition of CYP450 3A4 by duvelisib.', 'DDInter', 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vinca alkaloids).  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever duvelisib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if duvelisib is used in combination with sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297828/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2018):[2] "Product Information. Copiktra (duvelisib)." Verastem, Inc.  (2021):', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, Ribociclib, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251439, 'Echinacea', 'Paritaprevir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4.  Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea.  In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.  If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered.  Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297829/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004):  89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', NULL, 1767369485);
