INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251540, 'Paritaprevir', 'Lonafarnib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of paritaprevir, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors.  Patients should be monitored for signs and symptoms of hepatotoxicity (i.e., ALT and bilirubin elevations).', 'Metabolism', 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297972/', '[1] "Product Information. Viekira Pak (dasabuvir/ombitasvir/paritaprev/ritonav)." AbbVie US LLC  (2022):', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251541, 'Dasabuvir', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.  Lonafarnib is a weak P-gp inhibitor in vitro.', 'DDInter', 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297973/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals  (2020):', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Lamivudine, Glecaprevir, Simeprevir, Cabotegravir, Zanamivir, Valaciclovir, Velpatasvir, Dolutegravir, Oseltamivir, Acyclovir, Brincidofovir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251542, 'Paritaprevir', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.  The interaction has been studied with midazolam, a sensitive substrate of CYP450 3A4.', 'DDInter', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy.  Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure.  If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297975/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Bosutinib, Vemurafenib, Lapatinib, Selumetinib, Ribociclib, More', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251543, 'Dasabuvir', 'Lorlatinib', 'Moderate', 'Coadministration of lorlatinib with drugs that are P-glycoprotein (P-gp) substrates may decrease the plasma concentration and pharmacologic effect of the P-gp substrate.  The proposed mechanism is induction of P-gp mediated drug efflux by lorlatinib.', 'DDInter', 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.  If coadministration is required, consider dosage adjustments and clinical and laboratory monitoring whenever lorlatinib is added to or withdrawn from therapy.  P-gp substrate dosage may need to be increased in accordance with approved product labeling.', 'Absorption', 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/297976/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group  (2018):', 'Imatinib, Dabrafenib, Acalabrutinib, Dacomitinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Selumetinib, Vandetanib, Ibrutinib, More', 'Lamivudine, Cabotegravir, Simeprevir, Zanamivir, Valaciclovir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Remdesivir, Elbasvir, More', 1767369485);
