INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251600, 'Rucaparib', 'Paritaprevir', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of breast cancer resistance protein (BCRP) (e.g., rosuvastatin) and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., deferasirox).  The proposed mechanism, based on in vitro data, is decreased clearance due to rucaparib-mediated inhibition of these transport proteins.', 'DDInter', 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298063/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring.  http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852"   (2013):', 'Pertuzumab, Olaparib, Oxaliplatin, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Lamivudine, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Dolutegravir, Oseltamivir, Acyclovir, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251601, 'Dasabuvir', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with inhibitors of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may increase the risk of adverse effects, such as nausea, vomiting, diarrhea, and neutropenia.  The proposed mechanism involves increased exposure to the pharmacologically active metabolite SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1.  Genetic variants of the UGT1A1 gene such as the UGT1A1*28 allele lead to reduced UGT1A1 enzyme activity and patients who are homozygous for the UGT1A1*28 allele are at increased risk for neutropenia from SN-38.', 'DDInter', 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.  If coadministration is unavoidable, patients should be closely monitored for the development of severe nausea, vomiting, diarrhea, and neutropenia.  Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298064/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics  (2020):', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Valaciclovir, Zanamivir, Cabotegravir, Lenacapavir, More', 1767369485);
