INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251718, 'Encorafenib', 'Dienogest', 'Major', 'Coadministration with encorafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.  Because encorafenib can cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Therefore, females of reproductive potential should use an effective, nonhormonal method of contraception during treatment and for 2 weeks after the final dose of encorafenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298440/', '[1] "Product Information. Braftovi (encorafenib)." Array BioPharma Inc.  (2018):', 'Imatinib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, Bosutinib, Ribociclib, More', 'Megestrol acetate, Estetrol, Ulipristal, Estradiol, Megestrol acetate, Progesterone, Megestrol acetate, Progesterone, Estradiol', 1767369485);
