INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251770, 'Dienogest', 'Metreleptin', 'Moderate', 'Treatment with metreleptin, a recombinant analog of human leptin, may affect the pharmacokinetics of drugs that are primarily metabolized by CYP450 isoenzymes.', 'DDInter', 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.  Clinical and laboratory monitoring are recommended following the initiation, discontinuation or change of dosage of metreleptin, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298494/', '[1] "Product Information. Myalept (metreleptin)." Bristol-Myers Squibb  (2014):', 'Teduglutide, Lonafarnib, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Megestrol acetate, Estetrol, Estradiol, Megestrol acetate, Progesterone, Megestrol acetate, Progesterone, Estradiol', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251771, 'Dienogest', 'Mifepristone', 'Moderate', 'Mifepristone is a progesterone-receptor antagonist and will interfere with the effectiveness of hormonal contraceptives.', 'DDInter', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, only nonhormonal contraceptive methods should be used.  Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Antagonism', 'When mifepristone is prescribed daily to control hyperglycemia secondary to hypercortisolism in patients with endogenous Cushing''s syndrome, only nonhormonal contraceptive methods should be used.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298495/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated  (2012):', 'Danazol, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Ulipristal, Estetrol', 1767369485);
