INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251797, 'Dienogest', 'Pomalidomide', 'Major', 'Concomitant treatment with agents that can cause thrombosis such as erythropoiesis-stimulating agents, estrogens, selective estrogen receptor modulators, or C1 esterase inhibitors may potentiate the risk of venous thromboembolic events associated with the use of pomalidomide.', 'DDInter', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.  Prophylactic antithrombotic measures should be considered after assessing underlying risk factors such as health history and concomitant medications.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Agents that are known to cause thrombosis should be used with caution in multiple myeloma patients receiving pomalidomide.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298523/', '[1] "Product Information. Pomalyst (pomalidomide)." QLT Phototherapeutics Inc  (2013):', 'Antithymocyte immunoglobulin (rabbit), Anifrolumab, Ravulizumab, Diroximel fumarate, Methotrexate, Tacrolimus, Abatacept, Upadacitinib, Siponimod, Apremilast, Inebilizumab, More', 'Ulipristal, Norethisterone, Norelgestromin, Megestrol acetate, Norgestimate, Etynodiol, Levonorgestrel, Desogestrel, Etonogestrel, Norgestrel, Megestrol acetate, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251798, 'Dienogest', 'Posaconazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins.  The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298524/', '[1] Lazar JD, Wilner KD "Drug interactions with fluconazole." Rev Infect Dis 12 Suppl 3 (1990):  s327-33[2] Pillans PI, Sparrow MJ "Pregnancy associated with a combined oral contraceptive and itraconazole." N Z Med J 106 (1993):  436[3] Devenport MH, Crook D, Wynn V, Lees LJ "Metabolic effects of low-dose fluconazole in healthy female users and non-users of oral contraceptives." Br J Clin Pharmacol 27 (1989):  851-9[4] Sinofsky FE, Pasquale SA "The effect of fluconazole on circulating ethinyl estradiol levels in women taking oral contraceptives." Am J Obstet Gynecol 178 (1998):  300-4[5] Weisberg E "Interactions between oral contraceptives and antifungals antibacterials - Is contraceptive failure the result?." Clin Pharmacokinet 36 (1999):  309-13[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals  (2002):', 'Caspofungin, Micafungin, Amphotericin B, Miconazole, Oteseconazole, Flucytosine, Anidu