INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251885, 'Eluxadoline', 'Difenoxin', 'Moderate', 'The risk of constipation and serious constipation-related adverse reactions may be increased when eluxadoline is used with other drugs that are also associated with this adverse effect.', 'DDInter', 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.  Loperamide may be used occasionally for acute management of severe diarrhea, but chronic use is not recommended, and it should be discontinued immediately if constipation occurs.  Eluxadoline should also be discontinued if constipation occurs for more than 4 days.', 'Synergism', 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298619/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Viberzi (eluxadoline)." Actavis Pharma, Inc.  (2015):', 'Loperamide, Diphenoxylate, Difenoxin', 'Eluxadoline', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (251886, 'Esketamine', 'Difenoxin', 'Major', 'Concomitant use of esketamine with central nervous system (CNS) depressants may increase sedation and impairment of attention, judgment, thinking, reaction speed, and psychomotor skills.', 'DDInter', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants or other drugs that can cause sedation or dizziness.  Due to the risk of delayed or prolonged sedation and other adverse effects, patients should be monitored for at least 2 hours after esketamine administration, followed by an assessment to determine when the patient is considered clinically stable and ready to leave the healthcare setting.  Patients should be instructed not to engage in potentially hazardous activities that require complete mental alertness and motor coordination, such as driving a motor vehicle or operating machinery, until the next day after a restful sleep.', 'Synergism', 'Caution is advised and patients should be closely monitored during concomitant use of esketamine with CNS depressants or other drugs that can cause sedation or dizziness.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/298620/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "Canadian Product Information." O 0 (2015):[4] "Product Information. Spravato (esketamine)." Janssen Pharmaceuticals  (2019):', 'Sertraline, Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Viloxazine, Amoxapine, Imipramine, More', 'Eluxadoline', 1767369485);
