INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253426, 'Dichloralphenazone', 'Dextropropoxyphene', 'Major', 'Sedatives, tranquilizers, muscle relaxants, antidepressants, and other central nervous system (CNS) depressants may have additive CNS- and/or respiratory-depressant effects with propoxyphene.', 'DDInter', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.  Dosage reductions may be appropriate.  Patients should be monitored for potentially excessive or prolonged CNS and respiratory depression and other CNS adverse effects.  Patients should be warned not to exceed recommended dosages, to avoid alcohol, and to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', 'Caution is advised if propoxyphene is used with sedatives, tranquilizers, muscle relaxants, antidepressants, and other CNS depressants, particularly in the elderly and in patients with a history of emotional disturbances, suicidal ideation, or alcohol and drug abuse.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/300981/', '[1] Abernethy DR, Greenblatt DJ, Morse DS, Shader RI "Interaction of propoxyphene with diazepam, alprazolam and lorazepam." Br J Clin Pharmacol 19 (1985):  51-7[2] Hansen BS, Dam M, Brandt J, et al. "Influence of dextropropoxyphene on steady state serum levels and protein binding of three anti-epileptic drugs in man." Acta Neurol Scand 61 (1980):  357-67[3] Abernethy DR, Greenblatt DJ, Steel K, Shader RI "Impairment of hepatic drug oxidation by propoxyphene." Ann Intern Med 9