INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253519, 'Cabozantinib', 'Lumacaftor', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cabozantinib, which is a substrate of the isoenzyme.', 'DDInter', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.  If chronic coadministration is required, the daily dosage of cabozantinib should be increased in accordance with the prescribing information for the specific product.  For the treatment of progressive, metastatic medullary thyroid cancer (capsule formulation), the recommendation is to increase cabozantinib dosage by 40 mg (e.g., from 140 mg to 180 mg daily or from 100 mg to 140 mg daily) as tolerated, not to exceed 180 mg/day.  For the treatment of advanced renal cell carcinoma (RCC) in patients who have received prior anti-angiogenic therapy (tablet formulation), the recommendation is to increase cabozantinib dosage by 20 mg (e.g., from 60 mg to 80 mg daily or from 40 mg to 60 mg daily) as tolerated, not to exceed 80 mg/day.  The original dosage of cabozantinib may be resumed 2 to 3 days after discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Chronic use of potent CYP450 3A4 inducers during treatment with cabozantinib should be avoided whenever possible.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301091/', '[1] "Product Information. Cometriq (cabozantinib)." Exelixis Inc  (2012):[2] "Product Information. Cabometyx (cabozantinib)." Exelixis Inc  (2016):', 'Futibatinib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Pazopanib, Alectinib', 'Nitric Oxide, Doxapram, Ivacaftor, Elexacaftor', 1767369485);
