INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253638, 'Lumacaftor', 'Lapatinib', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.  If coadministration is required, a gradual dosage increase of lapatinib should be considered.  The prescribing information recommends titrating the lapatinib dosage gradually from 1250 mg/day up to 4500 mg/day or from 1500 mg/day up to 5500 mg/day depending on the indication and patient tolerability.  Based on pharmacokinetic studies, this dosage recommendation is predicted to adjust the lapatinib systemic exposure (AUC) to the range observed without inducers.  However, clinical data are lacking.  The dosage of lapatinib should be reduced over approximately 2 weeks to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'Concomitant use of lapatinib with potent CYP450 3A4 inducers should generally be avoided.', 'Major', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301227/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals  (2023):[2] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals Canada Inc  (2022):[3] "Product Information. Tyverb (lapatinib)." Novartis Pharmaceuticals UK Ltd  (2022):[4] "Product Information. Tykerb (laPAtinib)." Novartis Pharmaceuticals Australia Pty Ltd  (2022):[5] Smith DA, Koch KM, Arya N, Bowen CJ, Herendeen JM, Beelan A "Effects of ketoconazole and carbamazepine on lapatinib pharmacokinetics in healthy subjects." Br J Clin Pharmacol 67 (2009):  421-6', 'Futibatinib, Lenvatinib, Trametinib, Afatinib, Dacomitinib, Pazopanib, Alectinib', 'Nitric Oxide, Doxapram, Elexacaftor', 1767369485);
