INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253646, 'Lumacaftor', 'Linagliptin', 'Moderate', 'Coadministration with potent inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may significantly decrease the plasma concentrations of linagliptin, which is a substrate for both.', 'DDInter', 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided.  For patients requiring use of such drugs, an alternative to linagliptin is strongly recommended.  Other known inducers of CYP450 3A4 include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, modafinil, nafcillin, nevirapine, and various other anticonvulsants, although the extent to which they interact with linagliptin is unknown.  If concomitant use with these agents is necessary, glycemic control should be closely monitored.', 'Metabolism', 'Due to the potential for reduced efficacy of linagliptin, coadministration with potent CYP450 3A4 and/or P-glycoprotein inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301238/', '[1] "Product Information. Tradjenta (linagliptin)." Boehringer Ingelheim  (2011):', 'Guar gum, Pramlintide, Alogliptin, Rosuvastatin, Empagliflozin, Dulaglutide, Exenatide, Miglitol, Albiglutide, Ertugliflozin, Semaglutide, More', 'Nitric Oxide, Doxapram, Ivacaftor, Elexacaftor', 1767369485);
