INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253831, 'Estradiol', 'Tipiracil', 'Moderate', 'Coadministration with tipiracil-trifluridine may reduce the efficacy of contraceptive hormones.', 'DDInter', 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.  Female patients of childbearing potential using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant tipiracil-trifluridine treatment.  The manufacturer recommends use of additional barrier contraceptive methods during and for up to 6 months after tipiracil-trifluridine therapy.  No recommendations are available for women using hormone-release intrauterine systems or injectable progestin-only contraceptives.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Antagonism', 'Because trifluridine may cause fetal harm, it is particularly important that patients not become pregnant during tipiracil-trifluridine treatment.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301482/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0', 'Progesterone, Norgestimate, Etynodiol, Desogestrel, Dienogest, Conjugated estrogens, Estrone, Diethylstilbestrol, Chlorotrianisene, Bazedoxifene, Testosterone, More', NULL, 1767369485);
