INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253888, 'Tipiracil', 'Tafenoquine', 'Moderate', 'Coadministration with tafenoquine may increase the plasma concentrations of drugs that are substrates of organic cation transporter-2 (OCT2) or multidrug and toxin extrusion (MATE) transporters.  Clinical drug interaction studies with tafenoquine and OCT2 and MATE substrates have not been conducted in humans.  However, in vitro observations suggest the potential for increased concentrations of these substrates, which may result in increased toxicities.  Specifically, tafenoquine inhibited metformin transport via human OCT2, MATE-1, and MATE2-K transporters in vitro.', 'DDInter', 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided.  If coadministration is required, patients should be monitored for drug-related toxicities and dosage reductions considered as needed in accordance with the product labeling of the coadministered drugs.', 'Excretion', 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301586/', '[1] "Product Information. Krintafel (tafenoquine)." GlaxoSmithKline  (2018):', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Proguanil, More', NULL, 1767369485);
