INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253978, 'Fidaxomicin', 'Voxilaprevir', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.  When fidaxomicin 200 mg was administered in combination with cyclosporine 200 mg in 14 study subjects, fidaxomicin peak plasma concentration (Cmax) and systemic exposure (AUC) increased by an average of 4.2- and 1.9-fold, respectively, compared to when administered alone.  The Cmax and AUC of OP-1118 was increased by 9.5- and 4.1-fold, respectively, with cyclosporine.  Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.  However, concomitant use of a P-gp inhibitor had no attributable effect on safety or efficacy of fidaxomicin in controlled clinical trials.  No dosage adjustment is recommended when fidaxomicin is coadministered with P-gp inhibitors.', 'Absorption, Excretion', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter.', 'Minor', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301815/', '[1] "Product Information. Dificid (fidaxomicin)." Optimer Pharmaceuticals  (2011):', 'Rifamycin, Kanamycin, Amphotericin B, Miconazole, Polymyxin B, Nystatin, Vancomycin, Streptomycin, Rifaximin, Paromomycin, Neomycin', 'Lamivudine, Simeprevir, Amprenavir, Cabotegravir, Zanamivir, Valaciclovir, Rilpivirine, Etravirine, Dolutegravir, Oseltamivir, Cobicistat, More', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, management_text, mechanism_text, recommendation, risk_level, type, metabolism_info, source_url, reference_text, alternatives_a, alternatives_b, updated_at) VALUES (253979, 'Fluvastatin', 'Voxilaprevir', 'Moderate', 'Coadministration with sofosbuvir/velpatasvir/voxilaprevir may increase the plasma concentrations of all statins including atorvastatin, fluvastatin, lovastatin, and simvastatin.  The proposed mechanism is inhibition of organic anion transporting polypeptide (OATP) 1B1-mediated hepatic uptake of statins by velpatasvir and voxilaprevir.', 'DDInter', 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.  The lowest approved dosage of the statin should be used.  If higher dosages are required, the lowest effective statin dosage should be prescribed based on a risk/benefit assessment.', 'Distribution', 'Caution is advised during concomitant administration of atorvastatin, fluvastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with sofosbuvir/velpatasvir/voxilaprevir.', 'Moderate', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/301816/', '[1] "Product Information. Vosevi (sofosbuvir/velpatasvir/voxilaprevir)." Gilead Sciences  (2017):', 'Fenofibric acid, Mipomersen, Evinacumab, Evolocumab, Dextrothyroxine, Probucol, Cholestyramine, Ezetimibe, Clofibrate, Cerivastatin, Colestipol, More', 'Lamivudine, Paritaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Telaprevir, Velpatasvir, Rilpivirine, More', 1767369485);
