INSERT INTO drug_interactions (med_id, interaction_drug_name, interaction_dailymed_id, severity, description, source) VALUES
(26590, 'metformin', 'N/A', 'minor', '7 DRUG INTERACTIONS 7.1 Drug-Drug Interactions Metformin In patients with renal impairment, metformin can cause lactic acidosis. Iodinated contrast agents appear to increase the risk of metformin-induced lactic acidosis, possibly as a result of worsening renal function. Stop metformin at the time of, or prior to, iopamidol injection administration in patients with an eGFR between 30 and 60 mL/min/1.73 m 2; in patients with a history of hepatic impairment, alcoholism, or heart failure; or in patients who will be administered intra-arterial iodinated contrast agents.', 'DailyMed'),
