INSERT INTO drug_interactions (med_id, interaction_drug_name, interaction_dailymed_id, severity, description, source) VALUES
(26389, 'stiripentol', 'N/A', 'minor', 'Because of potential drug-drug interactions, consider dose adjustment of CYP1A2 substrates (e.g., theophylline, caffeine), CYP2B6 substrates (e.g., sertraline, thiotepa), and CYP3A4 substrates (e.g., midazolam, triazolam, quinidine), as clinically appropriate, when administered concomitantly with DIACOMIT.', 'DailyMed'),
