INSERT INTO drug_interactions (med_id, interaction_drug_name, interaction_dailymed_id, severity, description, source) VALUES
(20224, 'tolterodine', 'N/A', 'minor', '7.4 Other Drugs Metabolized by Cytochrome P450 Isoenzymes In vivo drug-interaction data show that tolterodine immediate release does not result in clinically relevant inhibition of CYP1A2, 2D6, 2C9, 2C19, or 3A4 as evidenced by lack of influence on the marker drugs caffeine, debrisoquine, S-warfarin, and omeprazole [ see CLINICAL PHARMACOLOGY ( 12.3) ].', 'DailyMed'),
(20224, 'nicotine', 'N/A', 'minor', 'May Require a Decrease in Dose at Cessation of Smoking Possible Mechanism Acetaminophen, caffeine, imipramine, oxazepam, pentazocine, propranolol, or other beta-blockers, theophylline Deinduction of hepatic enzymes on smoking cessation.', 'DailyMed'),
(20224, 'glecaprevir and pibrentasvir', 'N/A', 'major', '7.5 Drugs with No Observed Clinically Significant Interactions with MAVYRET No dose adjustment is required when MAVYRET is coadministered with the following medications: abacavir, amlodipine, caffeine, dextromethorphan, dolutegravir, elvitegravir/ cobicistat, emtricitabine, ethinyl estradiol of 20 mcg or less, felodipine, lamivudine, lamotrigine, losartan, midazolam, norethindrone or other progestin-only contraceptives, omeprazole, raltegravir, rilpivirine, sofosbuvir, tacrolimus, tenofovir alafenamide, tenofovir disoproxil fumarate, tolbutamide, and valsartan.', 'DailyMed'),
(20224, 'tipranavir', 'N/A', 'minor', 'A phenotypic cocktail study was conducted with 16 healthy volunteers to quantify the influence of 10 days of APTIVUS/ritonavir capsule administration on the activity of hepatic CYP1A2 (caffeine), 2C9 (warfarin), 2C19 (omeprazole), 2D6 (dextromethorphan) and the activity of intestinal and hepatic CYP3A4/5 (midazolam) and P-glycoprotein (P-gp) (digoxin).', 'DailyMed'),
(20224, 'migalastat hydrochloride', 'N/A', 'minor', '(7.1) 7.1 Effect of Other Drugs on GALAFOLD Co-administration of GALAFOLD with caffeine decreases migalastat AUC and Cmax [see Clinical Pharmacology (12.3)] which may reduce GALAFOLD efficacy. Avoid co-administration of GALAFOLD with caffeine at least 2 hours before and 2 hours after taking GALAFOLD [see Dosage and Administration (2.2)].', 'DailyMed'),
(20224, 'maribavir', 'N/A', 'major', '7.4 Drugs without Clinically Significant Interactions with LIVTENCITY No clinically significant interactions were observed in clinical drug-drug interaction studies of LIVTENCITY and ketoconazole, antacid, caffeine, warfarin, voriconazole, dextromethorphan, or midazolam [see Clinical Pharmacology (12.3)].', 'DailyMed'),
(30425, 'trovafloxacin mesylate', 'N/A', 'moderate', 'Patients should also be monitored for evidence of bleeding.Minor pharmacokinetic interactions without clinical significance have been observed with co-administration of TROVAN Tablets with caffeine, omeprazole and calcium carbonate.', 'DailyMed'),
