INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1098, 16480, 'Lactic acid', 'Water-Electrolyte Imbalance', 'Alkalinizing agents containing sodium can induce sodium and water retention and result in hypernatremia, hypokalemia, hyperosmolality, edema and aggravation of congestive heart failure.  Therapy with sodium- containing alkalinizing agents should not be used in patients with hypernatremia or fluid retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with alkalinizing agents is recommended.', '3', '"Product Information. Sodium Bicarbonate (sodium bicarbonate)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1099, 19061, 'Lactic acid', 'Water-Electrolyte Imbalance', 'Alkalinizing agents containing sodium can induce sodium and water retention and result in hypernatremia, hypokalemia, hyperosmolality, edema and aggravation of congestive heart failure.  Therapy with sodium- containing alkalinizing agents should not be used in patients with hypernatremia or fluid retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with alkalinizing agents is recommended.', '3', '"Product Information. Sodium Bicarbonate (sodium bicarbonate)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1100, 16480, 'Lactic acid', 'Acidosis, Lactic', 'Lactate metabolism results in production of bicarbonate.  Bicarbonate generation is impaired in patients with compromised liver function and/or disorders that decrease tissue perfusion and increase anoxia.  Therapy with sodium lactate should not be used in conditions in which lactate levels are increased (e.g., shock, congestive heart failure, respiratory alkalosis) or in which utilization of lactate is diminished (e.g., anoxia, beriberi).  Sodium lactate is not for use in the treatment of lactic acidosis.', '3', '"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1101, 19061, 'Lactic acid', 'Acidosis, Lactic', 'Lactate metabolism results in production of bicarbonate.  Bicarbonate generation is impaired in patients with compromised liver function and/or disorders that decrease tissue perfusion and increase anoxia.  Therapy with sodium lactate should not be used in conditions in which lactate levels are increased (e.g., shock, congestive heart failure, respiratory alkalosis) or in which utilization of lactate is diminished (e.g., anoxia, beriberi).  Sodium lactate is not for use in the treatment of lactic acidosis.', '3', '"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1102, 16480, 'Lactic acid', 'Acid-Base Imbalance', 'Alkalinizing agents act as proton acceptors and/or dissociate to provide bicarbonate ions.  Elimination of bicarbonate is decreased in patients with renal impairment and can result in metabolic alkalosis.  Symptoms of metabolic alkalosis include hyperirritability or tetany, arrhythmia, and/or seizures (altered pH = altered calcium), or lactic acidosis due to impaired oxygen release.  Therapy with alkalinizing agents should be administered with extreme caution in patients with compromised renal function.  Clinical monitoring of renal function, acid/base balance and electrolytes is recommended.', '2', '"Product Information. Urocit-K (potassium citrate)." Mission Pharmacal Company|"Product Information. Tham (tromethamine)." Abbott Pharmaceutical  (2001):|"Product Information. Sodium Lactate (sodium lactate)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
