INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1335, 3008, 'Lamotrigine', 'Hematologic Diseases', 'Reports of blood dyscrasias that may or may not be associated with multi-organ hypersensitivity (also known as DRESS) have been reported in patients treated with lamotrigine.  These have included neutropenia, leukopenia, anemia, thrombocytopenia, pancytopenia, and, rarely, aplastic anemia and pure red cell aplasia.', '2', '"Product Information. Lamictal (lamotrigine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
