INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1379, 3008, 'Lamotrigine', 'Meningitis', 'Therapy with lamotrigine increases the risk of aseptic meningitis.  Symptoms upon presentation included headache, fever, nausea, vomiting, and nuchal rigidity and usually occurred after treatment initiation.  In most cases, symptoms resolved after discontinuation of treatment but returned quickly and more severely with reexposure.  Some of the patients who developed aseptic meningitis had underlying diagnoses of systemic lupus erythematosus and other autoimmune diseases.', '2', '"Product Information. Lamictal (lamotrigine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
