INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1642, 832, 'Leflunomide', 'Peripheral Nervous System Diseases', 'Peripheral neuropathy has been reported during leflunomide therapy.  Patients older than 60 years, patients taking concomitant neurotoxic medications, and diabetics may be at an increased risk for peripheral neuropathy.  Therapy with leflunomide should be administered cautiously in these patients.  Patients should be monitored for symptoms of neuropathy and if confirmed, consider discontinuing therapy and taking measures according to clinical practice.', '3', '"Product Information. Arava (leflunomide)." Hoechst Marion Roussel  (2001):', 'DDInter', 0);
