INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1717, 6419, 'Antilymphocyte immunoglobulin (horse)', 'IMMUNE SUPPRESSION', 'Do not administer live vaccines to patients about to receive, receiving, or after treatment with lymphocyte immune globulin, anti-thy (equine).  Concomitant administration of lymphocyte immune globulin, anti-thy (equine) with live virus vaccines carries a potential of uncontrolled viral replication in the immunosuppressed patient.  There is insufficient information to fully define the extent of the risk, or the period of time during which the risk exists.  If administered, live viruses may interfere with lymphocyte immune globulin, anti-thy (equine) treatment.', '2', '"Product Information. Atgam (lymphocyte immune globulin, anti-thy (equine))." Pfizer U.S. Pharmaceuticals Group  (2010):', 'DDInter', 0);
