INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1032, 728, 'Lacosamide', 'Cardiovascular Diseases', 'Dose dependent prolongation in PR interval has been observed with the use of lacosamide.  This drug should be used with caution in patients with known conduction abnormalities (AV block, sick sinus syndrome) in patients with sodium channelopathies (e.g., Brugada syndrome), with severe cardiac disease (myocardial ischemia, heart failure, structural disease), or in treatment with other drugs that also prolong PR interval.  An ECG is recommended before beginning treatment, and after dose titration to a steady maintenance dose.', '2', '"Product Information. Vimpat (lacosamide)." UCB Pharma Inc  (2008):', 'DDInter', 0);
