INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1517, 7076, 'Lamivudine', 'Kidney Diseases', 'Patients with clinically significant renal impairment may be at greater risk for toxicities and adverse effects from nucleoside reverse transcriptase inhibitors (NRTIs) due to decreased drug clearance.  Dosage adjustments are recommended.  In addition, these patients should be monitored closely during NRTI therapy, and dosages adjusted further if necessary.', '3', '"Product Information. HIVID (zalcitabine)." Roche Laboratories  (2001):|"Product Information. Retrovir (zidovudine)." Glaxo Wellcome  (2001):|"Product Information. Zerit (stavudine)." Bristol-Myers Squibb  (2001):|"Product Information. Epivir (lamivudine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
