INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1595, 120, 'Lansoprazole', 'Hypomagnesemia 1, Intestinal', 'Symptomatic and asymptomatic hypomagnesemia has been reported rarely in patients treated with PPIs for at least 3 months, in most cases after a year of therapy.  Serious adverse events can include tetany, seizures, and arrhythmias.  Caution should be used in patients prone to magnesium imbalances such as patients taking other medications that can cause hypomagnesemia (e.g., diuretics).  Regular monitoring is recommended.', '2', '"Product Information. Prevacid (lansoprazole)." TAP Pharmaceuticals Inc  (2001):|"Product Information. Aciphex (rabeprazole)." Janssen Pharmaceuticals  (2001):|"Product Information. Protonix (pantoprazole)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Nexium (esomeprazole)." Astra-Zeneca Pharmaceuticals  (2001):|"Product Information. Omeprazole (omeprazole)." Mylan Pharmaceuticals Inc  (2003):|"Product Information. Kapidex (dexlansoprazole)." Takeda Pharmaceuticals America  (2009):', 'DDInter', 0);
