INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1794, 21700, 'Lenvatinib', 'Thromboembolism', 'In clinical trials, arterial thromboembolic events were reported in patients treated with lenvatinib.  It is recommended to discontinue lenvatinib following an arterial thrombotic event.  Care should be exercised when resuming lenvatinib after an arterial thromboembolic event as its safety has not been established and its use has not been studied in patients who have had an arterial thromboembolic event within the previous 6 months.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
