INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1800, 21700, 'Lenvatinib', 'Diarrhea', 'Cases of diarrhea have been reported with the use of lenvatinib, sometimes leading to interruption of therapy.  It is recommended to initiate prompt medical management for the development of diarrhea and to monitor for dehydration.  It might be necessary to interrupt treatment with lenvatinib or resume at a reduced dose or permanently discontinue treatment with lenvatinib if severe or fatal diarrhea persists despite medical management.  Care and close monitoring is recommended when using this agent in patients presenting diarrhea or at risk of developing diarrhea.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1801, 21701, 'Lenvatinib', 'Diarrhea', 'Cases of diarrhea have been reported with the use of lenvatinib, sometimes leading to interruption of therapy.  It is recommended to initiate prompt medical management for the development of diarrhea and to monitor for dehydration.  It might be necessary to interrupt treatment with lenvatinib or resume at a reduced dose or permanently discontinue treatment with lenvatinib if severe or fatal diarrhea persists despite medical management.  Care and close monitoring is recommended when using this agent in patients presenting diarrhea or at risk of developing diarrhea.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1802, 21700, 'Lenvatinib', 'Peptic Ulcer Perforation', 'Cases of gastrointestinal perforation or fistula have been reported with the use of lenvatinib.  Discontinue the use of lenvatinib in patients who develop gastrointestinal perforation or life-threatening fistula.  Close monitoring should be exercised when using this agent in patients at risk.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
