INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1804, 21700, 'Lenvatinib', 'Hepatic Insufficiency', 'Hepatic failure, including fatal events have been reported with the use of lenvatinib.  No dose adjustment is recommended in patients with mild or moderate hepatic impairment.  Dose adjustment is needed in patients with severe hepatic impairment.  It is recommended to monitor liver function before initiation of therapy with lenvatinib and regularly thereafter as clinically indicated.  It might be necessary to temporarily withhold the use of lenvatinib and either resume at a reduced dose or discontinue treatment with lenvatinib depending on the severity and persistence of hepatic impairment.  Discontinue treatment with lenvatinib for hepatic failure.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1805, 21701, 'Lenvatinib', 'Hepatic Insufficiency', 'Hepatic failure, including fatal events have been reported with the use of lenvatinib.  No dose adjustment is recommended in patients with mild or moderate hepatic impairment.  Dose adjustment is needed in patients with severe hepatic impairment.  It is recommended to monitor liver function before initiation of therapy with lenvatinib and regularly thereafter as clinically indicated.  It might be necessary to temporarily withhold the use of lenvatinib and either resume at a reduced dose or discontinue treatment with lenvatinib depending on the severity and persistence of hepatic impairment.  Discontinue treatment with lenvatinib for hepatic failure.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1806, 21700, 'Lenvatinib', 'Hypertension', 'The use of lenvatinib may cause hypertension.  It is recommended to control blood pressure prior to treatment with lenvatinib and monitor blood pressure regularly during treatment as medically indicated.  Withhold treatment with lenvatinib for Grade 3 hypertension despite optimal antihypertensive therapy.  If clinically appropriate, resume at a reduced dose when hypertension is controlled and discontinue treatment for hypertension that becomes life-threatening.  Care and close monitoring of blood pressure is recommended.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
