INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1808, 21700, 'Lenvatinib', 'Hypocalcemia', 'Cases of hypocalcemia have been reported with the use of lenvatinib.  It is recommended to monitor blood calcium levels at least monthly and replace calcium as necessary during treatment and to interrupt and adjust dosing as necessary depending on severity, presence of ECG changes, and persistence of hypocalcemia.  Care should be exercised when using this agent in patients at risk.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1809, 21701, 'Lenvatinib', 'Hypocalcemia', 'Cases of hypocalcemia have been reported with the use of lenvatinib.  It is recommended to monitor blood calcium levels at least monthly and replace calcium as necessary during treatment and to interrupt and adjust dosing as necessary depending on severity, presence of ECG changes, and persistence of hypocalcemia.  Care should be exercised when using this agent in patients at risk.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1810, 21700, 'Lenvatinib', 'Hypothyroidism', 'The use of lenvatinib impairs exogenous thyroid suppression and may cause hypothyroidism.  It is recommended to monitor thyroid function before initiation of, and at least monthly throughout, treatment with lenvatinib.  Care should be taken when using this agent in patients with thyroid dysfunction and treatment of hypothyroidism should be made according to standard medical practice to maintain a euthyroid state.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
