INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1812, 21700, 'Lenvatinib', 'Proteinuria', 'Proteinuria has been reported with the use of lenvatinib.  It is recommended to monitor for proteinuria before initiation of, and periodically throughout treatment.  Withhold lenvatinib for >=2 grams of proteinuria/24 hours and resume at a reduced dose when proteinuria is <2 gm/24 hours.  Discontinue lenvatinib for nephrotic syndrome.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
