INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1816, 21700, 'Lenvatinib', 'Kidney Diseases', 'Cases of renal impairment have been reported with the use of lenvatinib.  No dose adjustment is recommended in patients with mild or moderate renal impairment.  Dose adjustment is needed in patients with severe renal impairment.  It might be necessary to temporarily withhold the use of lenvatinib and either resume at a reduced dose or discontinue treatment with lenvatinib depending on the severity and persistence of renal impairment.  Care and close monitoring is recommended when using lenvatinib in patients with end stage renal disease as these patients have not been studied.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
