INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1818, 21700, 'Lenvatinib', 'Posterior Leukoencephalopathy Syndrome', 'The use of lenvatinib may cause reversible posterior leukoencephalopathy syndrome (RPLS).  Consider this syndrome in any patient presenting with seizures, headache, visual disturbances, confusion or altered mental function.  Confirm the diagnosis of RPLS with MRI and if positive, withhold therapy for RPLS until fully resolved.  Upon resolution, resume at a reduced dose or discontinue treatment depending on the severity and persistence of neurologic symptoms and administer appropriate treatment as clinically indicated.', '2', '"Product Information. Lenvima (lenvatinib)." Eisai Inc  (2015):', 'DDInter', 0);
