INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1936, 7612, 'Leuprolide', 'Cardiovascular Diseases', 'Increased risk of developing myocardial infarction, sudden cardiac death and stroke has been reported in association with the use of GnRH agonists such as leuprolide, and triptorelin.  Cardiovascular risk factors should be evaluated carefully before treatment initiation.  Patients should be monitored for symptoms and signs suggestive of development or cardiovascular disease and should be managed according to current clinical practice.', '2', '"Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc  (2002):|"Product Information. Trelstar (triptorelin)." Pharmacia and Upjohn  (2001):|"Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):', 'DDInter', 0);
