INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (1944, 7612, 'Leuprolide', 'Long QT Syndrome', 'Androgen deprivation therapy may prolong the QT/QTc interval.  Health care providers should consider if benefits of therapy outweigh the potential risks in patients with congenital long QT syndrome, congestive heart failure, electrolyte abnormalities, or taking other drugs that can also prolong the QT interval.  Consider periodic monitoring of EKG and electrolytes.', '2', '"Product Information. Lupron (leuprolide)." TAP Pharmaceuticals Inc  (2002):|"Product Information. Eligard (leuprolide)." Sanofi Winthrop Pharmaceuticals  (2002):', 'DDInter', 0);
