INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3055, 4490, 'Levothyroxine', 'Diabetes Mellitus', 'Since hypothyroidism often obscure or diminish the signs and symptoms of endocrine disorders such as diabetes mellitus or diabetes insipidus, thyroid replacement therapy may aggravate the intensity of symptoms associated with these conditions.  Patients with endocrine disorders should be monitored more closely during thyroid hormone therapy, and their medication regimen adjusted accordingly.  If thyroid medication is later discontinued, a downward readjustment of insulin or oral hypoglycemic agent may be necessary in patients with diabetes mellitus to avoid hypoglycemia.', '2', '"Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical  (2002):|"Product Information. Cytomel (liothyronine)." Monarch Pharmaceuticals Inc  (2001):|"Product Information. Armour Thyroid (thyroid desiccated)." Forest Pharmaceuticals  (2022):|"Product Information. Thyrolar (liotrix)." Forest Pharmaceuticals  (2001):', 'DDInter', 0);
