INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3715, 6423, 'Loperamide', 'Water-Electrolyte Imbalance', 'Diarrhea can cause severe dehydration and electrolyte imbalance. Fluid accumulation within the GI track due to antiperistaltic-associated decrease in peristalsis can further aggravate dehydration and electrolyte imbalance. Antiperistaltic agents should be administered cautiously in patients with electrolyte imbalance and rehydration and electrolyte replacement should be initiated prior to initiation of therapy.', '2', '"Product Information. Imodium (loperamide)." Janssen Pharmaceuticals  (2001):|"Product Information. Kaopectate (attapulgite)." Pharmacia and Upjohn  (2001):|"Product Information. Pepto-Bismol (bismuth subsalicylate)." Procter and Gamble Pharmaceuticals  (2001):', 'DDInter', 0);
