INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3725, 6423, 'Loperamide', 'Megacolon, Toxic', 'Decreased intestinal motility and prolonged transit time have resulted in toxic megacolon in patients with acute ulcerative colitis.  Paralytic ileus has also occurred.  Antiperistaltic agent GI motility and prolongs transit time and therapy should be administered cautiously in these patients.', '2', '"Product Information. Imodium (loperamide)." Janssen Pharmaceuticals  (2001):|"Product Information. Kaopectate (attapulgite)." Pharmacia and Upjohn  (2001):|"Product Information. Pepto-Bismol (bismuth subsalicylate)." Procter and Gamble Pharmaceuticals  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):', 'DDInter', 0);
