INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3827, 5798, 'Lithium carbonate', 'Dehydration', 'Lithium reduces the ability of the kidney to concentrate urine and produces a mild diabetes insipidus and polyuria.  Therefore, patients with preexisting dehydration are more prone to lithium retention and toxicity.  Therapy with lithium should be administered cautiously in dehydrated patients and may need to be discontinued until improvement in hydration status.  Careful monitoring of lithium levels are recommended.  Patients with sweating, diarrhea or concomitant infection with fever may need a temporary reduction or cessation of medication.', '3', '"Product Information. Eskalith (lithium)." SmithKline Beecham  (2002):|"Product Information. Lithobid (lithium)." Ciba-Geigy Pharmaceuticals  (2002):|Martinez EJ, Sinnott JT, 4th  Rodriguez-Paz G, Oehler RL "Lithium-induced nephrogenic diabetes insipidus treated with indomethacin." South Med J 86 (1993):  971-3', 'DDInter', 0);
