INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3831, 5798, 'Lithium carbonate', 'Hypothyroidism', 'The use of lithium has been associated with hypothyroidism in 5% to 15% of patient as it blocks the release of thyroxine (T4) and triiodothyronine (T3) mediated by thyrotropin.  Therapy with lithium should be administered cautiously in patients with preexisting hypothyroidism.  Careful monitoring of the thyroid function is recommended and supplemental thyroid treatment may be required.', '3', '"Product Information. Eskalith (lithium)." SmithKline Beecham  (2002):|"Product Information. Lithobid (lithium)." Ciba-Geigy Pharmaceuticals  (2002):', 'DDInter', 0);
