INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3833, 5798, 'Lithium carbonate', 'Kidney Diseases', 'Lithium is primarily eliminated by the kidneys and should generally not be administered to patients with significant renal dysfunction.  Additionally, morphological changes with glomerular and interstitial fibrosis and nephron atrophy have been reported in patients on chronic lithium therapy, although the association has not been clearly established.  Kidney function should be assessed in patients prior to starting therapy, during and thereafter and routine urinalysis and renal function tests are recommended.  Progressive or sudden changes in renal function indicate the need for reevaluation of treatment.  Monitoring lithium serum levels is also recommended.', '3', '"Product Information. Eskalith (lithium)." SmithKline Beecham  (2002):|"Product Information. Lithobid (lithium)." Ciba-Geigy Pharmaceuticals  (2002):|Braden GL. Lithium induced renal diseases. In: Greenberg A, ed. "Primer on Kidney Diseases." San Diego, CA: Academic Press Inc.  (1994):  173-4', 'DDInter', 0);
