INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3849, 0, 'Loracarbef', 'Diseases requiring dialysis', 'Most cephalosporin antibiotics are removed by hemodialysis.  Doses should either be scheduled for administration after dialysis or supplemental doses be given after dialysis.  Cefonicid, cefixime, and ceftriaxone are not significantly removed by hemodialysis.', '2', '"Product Information. Ceclor (cefaclor)." Lilly, Eli and Company  (2002):|"Product Information. Duricef (cefadroxil)." Bristol-Myers Squibb  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Suprax (cefixime)." Lupin Pharmaceuticals Inc  (2002):|"Product Information. Monocid (cefonicid)." SmithKline Beecham  (2002):|"Product Information. Cefobid (cefoperazone)." Roerig Division  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Vantin (cefpodoxime)." Pharmacia and Upjohn  (2002):|"Product Information. Cefzil (cefprozil)." Bristol-Myers Squibb  (2002):|"Product Information. Tazicef (ceftazidime)." SmithKline Beecham  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Rocephin (ceftriaxone)." Roche Laboratories  (2002):|"Product Information. Keflex (cephalexin)." Dista Products Company  (2002):|"Product Information. Velosef (cephradine)." Apothecon Inc  (2002):|"Product Information. Keflin (cephalothin)." Lilly, Eli and Company  (2002):|"Product Information. Cefadyl (cephapirin)." Apothecon Inc  (2002):|"Product Information. Lorabid (loracarbef)." Lilly, Eli and Company  (2002):|"Product Information. Mandol (cefamandole)." Lilly, Eli and Company  (2001):|"Product Information. Cedax (ceftibuten)." Schering-Pl