INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3850, 0, 'Loracarbef', 'Liver Diseases', 'Cases of hepatitis have been reported with the use of certain cephalosporins.  Transient rise in AST, ALT, and alkaline phosphatase levels have also been observed.  Caution and monitoring is recommended when these agents are prescribed to patients with hepatic disorders.', '2', '"Product Information. Vantin (cefpodoxime)." Pharmacia and Upjohn|"Product Information. Ceclor (cefaclor)." Lilly, Eli and Company  (2002):|"Product Information. Duricef (cefadroxil)." Bristol-Myers Squibb  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Kefzol (cefazolin)." Lilly, Eli and Company  (2002):|"Product Information. Suprax (cefixime)." Lupin Pharmaceuticals Inc  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Cefzil (cefprozil)." Bristol-Myers Squibb  (2002):|"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Ceftin (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Zinacef (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Keflex (cephalexin)." Dista Products Company  (2002):|"Product Information. Cedax (ceftibuten)." Schering-Plough  (2001):|"Product Information. Maxipime (cefepime)." Bristol-Myers Squibb  (2001):|"Product Information. Omnicef (cefdinir)." Parke-Davis  (2001):|"Product Information. Ceclor CD (cefaclor)." Dura Pharmaceuticals  (2001):|"Product Information. Spectracef (cefditoren)." TAP Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (3851, 2702, 'Loratadine', 'Liver Diseases', 'Loratadine is primarily converted by the liver to an active metabolite, and both parent drug and metabolite are excreted in the urine.  Patients with renal and/or liver disease may be at greater risk for adverse effects from loratadine due to drug and metabolite accumulation.  The manufacturer recommends one-half the regular dosage initially in patients with liver failure or decreased renal function (GFR < 30 mL/min).  Due to the differential effects of hepatic impairment on the serum half-life and clearance of loratadine and pseudoephedrine, the use of products containing a fixed combination of these drugs is not recommended in the presence of liver failure.', '2', 'Matzke GR, Halstenson CE, Opsahl JA, Hilbert J, Perentesis G, Radwanski E, Zampaglione N "Pharmacokinetics of loratadine in patients with renal insufficiency." J Clin Pharmacol 30 (1990):  364-71|Clissold SP, Sorkin EM, Goa KL "Loratadine. A preliminary review of its pharmacodynamic properties and therapeutic efficacy." Drugs 37 (1989):  42-57|Hilbert J, Moritzen V, Parks A, Radwanski E, Perentesis G, Symchowicz S, Zampaglione N "The pharmacokinetics of loratadine in normal geriatric volunteers." J Int Med Res 16 (1988):  50-60|"Product Information. Claritin (loratadine)." Schering Corporation  (2002):|Schiano TD, Bellary SV, Cassidy MJ, Thomas RM, Black M "Subfulminant liver failure and severe hepatotoxicity caused by loratadine use." Ann Intern Med 125 (1996):  738-40|Monroe EW "Safety and efficacy of loratadine in urticaria." Int J Dermatol 35 (1996):  837-41', 'DDInter', 0);
