INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4200, 1162, 'Linezolid', 'Kidney Diseases', 'Linezolid is primarily metabolized by the liver and subsequently eliminated by the kidney.  The pharmacokinetics of the parent drug are not altered in patients with impaired renal function and, therefore, no dosage adjustments are necessary.  However, accumulation of the two primary metabolites may occur in such patients, the amount of which increases with the degree of renal impairment.  The clinical significance of metabolite accumulation has not been determined.  Therapy with linezolid should be administered cautiously in patients with renal impairment and only if benefits outweigh the potential risks.', '2', '"Product Information. Zyvox (linezolid)." Pharmacia and Upjohn  (2001):', 'DDInter', 0);
