INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4277, 5593, 'Acarbose', 'Liver Diseases', 'The use of acarbose has been associated with dose-related elevations in serum transaminase levels and, rarely, hyperbilirubinemia and jaundice.  Hepatic adverse effects may be more likely to occur at dosages exceeding 50 mg three times a day.  Therapy with acarbose should be administered cautiously in patients with liver disease.  Monitoring of serum transaminases is recommended every 3 months during the first year of treatment and periodically thereafter.  If hepatotoxicity is suspected at any time, a dosage reduction or withdrawal of therapy may be indicated.', '2', 'Hollander P "Safety profile of acarbose, an alpha-glucosidase inhibitor." Drugs 44 Suppl 3 (1992):  47-53|Clissold SP, Edwards C "Acarbose. A preliminary review of its pharmacodynamic and pharmacokinetic properties, and therapeutic potential." Drugs 35 (1988):  214-43|Balfour JA, McTavish D "Acarbose. An update of its pharmacology and therapeutic use in diabetes mellitus [published erratum appears in Drugs 1994 Dec;48(6):929]." Drugs 46 (1993):  1025-54|"Product Information. Precose (acarbose)." Bayer  (2001):|Andrade RJ, Lucena MI, Rodriguezmendizabal M "Hepatic injury caused by acarbose." Ann Intern Med 124 (1996):  931', 'DDInter', 0);
INSERT OR REPLACE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4278, 24143, 'Acarbose', 'Liver Diseases', 'The use of acarbose has been associated with dose-related elevations in serum transaminase levels and, rarely, hyperbilirubinemia and jaundice.  Hepatic adverse effects may be more likely to occur at dosages exceeding 50 mg three times a day.  Therapy with acarbose should be administered cautiously in patients with liver disease.  Monitoring of serum transaminases is recommended every 3 months during the first year of treatment and periodically thereafter.  If hepatotoxicity is suspected at any time, a dosage reduction or withdrawal of therapy may be indicated.', '2', 'Hollander P "Safety profile of acarbose, an alpha-glucosidase inhibitor." Drugs 44 Suppl 3 (1992):  47-53|Clissold SP, Edwards C "Acarbose. A preliminary review of its pharmacodynamic and pharmacokinetic properties, and therapeutic potential." Drugs 35 (1988):  214-43|Balfour JA, McTavish D "Acarbose. An update of its pharmacology and therapeutic use in diabetes mellitus [published erratum appears in Drugs 1994 Dec;48(6):929]." Drugs 46 (1993):  1025-54|"Product Information. Precose (acarbose)." Bayer  (2001):|Andrade RJ, Lucena MI, Rodriguezmendizabal M "Hepatic injury caused by acarbose." Ann Intern Med 124 (1996):  931', 'DDInter', 0);
